g = 0.4 + 0.4*sin(time*5.307)*x_int;
b = 0.4 + 0.4*sin(time*4.959)*y_int;

xs = if(above(xs,1000),0 ,xs);
ys = if(above(ys,1000),0 ,ys);

frame_eqs_eel:
